import{_ as e,c as t,a as i,o as n}from"./app-BsmbRiif.js";const l="/assets/1-C0UM5cIi.png",s="/assets/2-DEFSpbu8.png",r={};function c(o,a){return n(),t("div",null,a[0]||(a[0]=[i('<h1 id="拼团交易平台系统" tabindex="-1"><a class="header-anchor" href="#拼团交易平台系统"><span>拼团交易平台系统</span></a></h1><p>拼团交易平台系统，是我在日常使用拼多多、腾讯、京东等服务平台，交易支付时候，了解到这样的一种营销手段。它可以通过用户自传播方式增强交易量，也是拼多多最开始起家形成巨大规模的一个业务逻辑。因此非常感兴趣这样的系统，所以根据大厂分享的资料、与对应的架构师UP进行交流学习了，设计了这样一套系统。 该系统采用了 DDD 领域驱动设计进行建模，拆分领域模块边界，形成；活动领域、人群领域、交易领域，来构建拼团营销交易流程，达到试算、锁单、结算等步骤流程。这个过程中提炼了通用设计模式，规则树、责任链，可以非常有效的统一的治理流程编排实现。</p><h2 id="系统设计" tabindex="-1"><a class="header-anchor" href="#系统设计"><span>系统设计</span></a></h2><h3 id="功能流程" tabindex="-1"><a class="header-anchor" href="#功能流程"><span>功能流程</span></a></h3><p><img src="'+l+'" alt="测试图片"></p><ul><li>首先，由运营配置商品拼团活动，增加折扣方式。因为有人群标签的过滤，所以可以控制哪些人可参与拼团。</li><li>之后，用户可见拼团商品并参与拼团。用户可自主分享拼团或者等待拼团。因为拼团有非常大的折扣刺激用户自主分享，以此可以节省营销推广费用。</li><li>最后，拼团完成，触达商品发货。这里有两种，一种运营手段是拼团成团稀有性，必须打成拼团才可以。另外一种是虚拟拼团，无论是否打成，到时都完成拼团。</li></ul><h3 id="研发设计流程图" tabindex="-1"><a class="header-anchor" href="#研发设计流程图"><span>研发设计流程图</span></a></h3><p><img src="'+s+'" alt="测试图片"></p><h3 id="项目描述" tabindex="-1"><a class="header-anchor" href="#项目描述"><span>项目描述</span></a></h3><p>该项目参考拼多多交易购物拼团场景，调研中大厂相关营销业务场景和技术架构方案，设计实现了本套拼团营销服务系统，支持各类营销优惠（直减、折扣、N元购）。该系统以面向对象开发，运用 DDD 拆分领域边界，使用设计模式设计服务功能。提高系统的扩展性和可维护性。</p>',10)]))}const h=e(r,[["render",c]]),p=JSON.parse('{"path":"/blogs/xiangmukaifa/pintuanxitong/guide.html","title":"拼团交易平台系统开发-学习日志","lang":"en-US","frontmatter":{"title":"拼团交易平台系统开发-学习日志","date":"2019/04/09","tags":["拼团系统开发"],"categories":["项目开发"]},"headers":[{"level":2,"title":"系统设计","slug":"系统设计","link":"#系统设计","children":[{"level":3,"title":"功能流程","slug":"功能流程","link":"#功能流程","children":[]},{"level":3,"title":"研发设计流程图","slug":"研发设计流程图","link":"#研发设计流程图","children":[]},{"level":3,"title":"项目描述","slug":"项目描述","link":"#项目描述","children":[]}]}],"git":{"createdTime":1748940795000,"updatedTime":1749459287000,"contributors":[{"name":"zxy","email":"zxy0613zxy@outlook.com","commits":2}]},"filePathRelative":"blogs/xiangmukaifa/pintuanxitong/guide.md"}');export{h as comp,p as data};
